#include <pybind11/complex.h>
#include <pybind11/pybind11.h>
#include <pybind11/stl.h>

namespace py = pybind11;

#include <gnuradio/iio/ad9081_sink.h>
// pydoc.h is automatically generated in the build directory
#include <ad9081_sink_pydoc.h>

void bind_ad9081_sink(py::module& m)
{

    using ad9081_sink = gr::iio::ad9081_sink;

    py::class_<ad9081_sink, gr::basic_block, std::shared_ptr<ad9081_sink>>(
        m, "ad9081_sink", D(ad9081_sink))

        .def_readonly_static("MAX_CHANNEL_COUNT", &ad9081_sink::MAX_CHANNEL_COUNT)

        .def(py::init(&ad9081_sink::make),
             py::arg("uri"),
             py::arg("en"),
             py::arg("buffer_size") = 65536UL,
             py::arg("cyclic") = false,
             D(ad9081_sink, make))

        .def("set_main_nco_freq",
             &ad9081_sink::set_main_nco_freq,
             py::arg("nco"),
             py::arg("freq"),
             D(ad9081_sink, set_main_nco_freq))

        .def("set_main_nco_phase",
             &ad9081_sink::set_main_nco_phase,
             py::arg("nco"),
             py::arg("phase"),
             D(ad9081_sink, set_main_nco_phase))

        .def("set_channel_nco_freq",
             &ad9081_sink::set_channel_nco_freq,
             py::arg("nco"),
             py::arg("freq"),
             D(ad9081_sink, set_channel_nco_freq))

        .def("set_channel_nco_phase",
             &ad9081_sink::set_channel_nco_phase,
             py::arg("nco"),
             py::arg("freq"),
             D(ad9081_sink, set_channel_nco_phase));
}
