#include <pybind11/complex.h>
#include <pybind11/pybind11.h>
#include <pybind11/stl.h>

namespace py = pybind11;

#include <gnuradio/iio/ad9081_source.h>
// pydoc.h is automatically generated in the build directory
#include <ad9081_source_pydoc.h>

void bind_ad9081_source(py::module& m)
{

    using ad9081_source = gr::iio::ad9081_source;

    py::class_<ad9081_source, gr::basic_block, std::shared_ptr<ad9081_source>>(
        m, "ad9081_source", D(ad9081_source))

        .def_readonly_static("MAX_CHANNEL_COUNT", &ad9081_source::MAX_CHANNEL_COUNT)

        .def(py::init(&ad9081_source::make),
             py::arg("uri"),
             py::arg("en"),
             py::arg("buffer_size") = 65536UL,
             D(ad9081_source, make))

        .def("set_main_nco_freq",
             &ad9081_source::set_main_nco_freq,
             py::arg("nco"),
             py::arg("freq"),
             D(ad9081_source, set_main_nco_freq))

        .def("set_main_nco_phase",
             &ad9081_source::set_main_nco_phase,
             py::arg("nco"),
             py::arg("phase"),
             D(ad9081_source, set_main_nco_phase))

        .def("set_channel_nco_freq",
             &ad9081_source::set_channel_nco_freq,
             py::arg("nco"),
             py::arg("freq"),
             D(ad9081_source, set_channel_nco_freq))

        .def("set_channel_nco_phase",
             &ad9081_source::set_channel_nco_phase,
             py::arg("nco"),
             py::arg("freq"),
             D(ad9081_source, set_channel_nco_phase))

        .def("set_nyquist_zone",
             &ad9081_source::set_nyquist_zone,
             py::arg("odd"),
             D(ad9081_source, set_nyquist_zone))

        .def("set_filter_source_file",
             &ad9081_source::set_filter_source_file,
             py::arg("src_file"),
             D(ad9081_source, set_filter_source_file));
}
